mapper.select:
SELECT DISTINCT t.id, t.parent_id AS parentId, t.enterprise_id AS enterpriseId, t.department_code AS departmentCode, t.department_name AS departmentName, t.create_time AS createTime, t.update_time AS updateTime, t.create_user AS createUser, t.update_user AS updateUser, t.parameter1, t.parameter2, t.is_sync AS isSync, t2.enterprise_name AS enterpriseName FROM t_enterprise_department t LEFT JOIN t_enterprise t2 ON t.enterprise_id= t2.id LEFT JOIN t_sys_user t3 on t2.id=t3.enterprise_id LEFT JOIN t_sys_user_enterprise t4 on t3.id=t4.user_id
t.id=#{id}
and t.enterprise_id=#{enterpriseId}
and t.department_code=#{departmentCode}
and t.department_name like '%${departmentName}%'
and t2.enterprise_name like '%${enterpriseName}%'
and t3.id=#{userId}
order by t.department_code

SELECT DISTINCT t.id AS id, t.department_name AS name, t.parent_id as parentId FROM t_enterprise_department t LEFT JOIN t_enterprise t2 ON t.enterprise_id = t2.id LEFT JOIN t_sys_user t3 ON t2.id = t3.enterprise_id LEFT JOIN t_sys_user_enterprise t4 ON t3.id = t4.user_id
t.id=#{id}
and t.enterprise_id=#{enterpriseId}
and t.department_code=#{departmentCode}
and t.department_name like '%${departmentName}%'
and t2.enterprise_name like '%${enterpriseName}%'
and t3.id=#{userId}
order by t.department_code
