mapper.select:
SELECT DISTINCT t.id, t.department_id AS departmentId, t.job_code AS jobCode, t.job_name AS jobName, t.create_time AS createTime, t.update_time AS updateTime, t.create_user AS createUser, t.update_user AS updateUser, t.parameter1, t.parameter2, t.is_sync AS isSync, t2.department_name as departmentName, t3.id as enterpriseId, t3.enterprise_name as enterpriseName FROM t_enterprise_job t LEFT JOIN t_enterprise_department t2 on t.department_id=t2.id LEFT JOIN t_enterprise t3 on t2.enterprise_id=t3.id LEFT JOIN t_sys_user t4 on t3.id=t4.enterprise_id LEFT JOIN t_sys_user_enterprise t5 on t4.id=t5.user_id
t.id=#{params.id}
and t.job_code=#{params.jobCode}
and t.job_name like '%${params.jobName}%'
and t.department_id=#{params.departmentId}
and t3.id=#{params.enterpriseId}
and t4.id=#{params.userId}
order by t.job_code

SELECT DISTINCT t.id, t.department_id AS departmentId, t.job_code AS jobCode, t.job_name AS jobName, t.create_time AS createTime, t.update_time AS updateTime, t.create_user AS createUser, t.update_user AS updateUser, t.parameter1, t.parameter2, t.is_sync AS isSync, t2.department_name as departmentName, t3.enterprise_name as enterpriseName FROM t_enterprise_job t LEFT JOIN t_enterprise_department t2 on t.department_id=t2.id LEFT JOIN t_enterprise t3 on t2.enterprise_id=t3.id LEFT JOIN t_sys_user t4 on t3.id=t4.enterprise_id LEFT JOIN t_sys_user_enterprise t5 on t4.id=t5.user_id
t.id=#{params.id}
and t.job_code=#{params.jobCode}
and t.job_name like '%${params.jobName}%'
and t3.id=#{params.enterpriseId}
and t4.id=#{params.userId}
order by t.job_code

SELECT t.id AS id, t.job_name AS name FROM t_app_enterprise_job t LEFT JOIN t_enterprise_department t2 on t.department_id=t2.id LEFT JOIN t_enterprise t3 on t2.enterprise_id=t3.id LEFT JOIN t_sys_user t4 on t3.id=t4.enterprise_id LEFT JOIN t_sys_user_enterprise t5 on t4.id=t5.user_id
and t2.enterprise_id=#{enterpriseId}
and t4.id=#{params.userId}
order by t.create_time desc
