mapper.select:
SELECT DISTINCT t.id, t.login_name AS loginName, t.`name`, t.`password`, t.salt, t.sex, t.phone, t.email, t.user_type AS userType, t.`status`, t.expired, t.enterprise_id AS enterpriseId, t.department_id AS departmentId, t.job_id AS jobId, t.is_leader AS isLeader, t.create_time AS createTime, t.update_time AS updateTine, t.`password` AS userPwd, t3.id AS roleId, t3.`name` AS roleName, t3.description AS roleDescription, t3.seq AS roleSeq, t3.`status` AS roleStatus, t4.enterprise_name AS enterpriseName, t5.department_name AS departmentName, t6.job_name AS jobName, group_concat(t3.`name`) AS rolesList FROM t_sys_user t LEFT JOIN t_sys_user_role t2 ON t.id = t2.user_id LEFT JOIN t_sys_role t3 ON t2.role_id = t3.id LEFT JOIN t_enterprise t4 ON t.enterprise_id = t4.id LEFT JOIN t_enterprise_department t5 ON t.department_id = t5.id LEFT JOIN t_enterprise_job t6 ON t.job_id = t6.id
t.login_name like concat('%',#{params.name},'%') or t.name like concat('%',#{params.name},'%')
and t.enterprise_id = #{params.enterpriseId}
and t.department_id= #{params.departmentId}
and (t.create_time between concat(#{params.startTime},'00:00:00') and concat(#{params.endTime},"00:00:00"))
GROUP BY t.id

SELECT DISTINCT t.id, t.login_name AS loginName, t.`name`, t.`password`, t.salt, t.sex, t.phone, t.email, t.user_type AS userType, t.`status`, t.expired, t.enterprise_id AS enterpriseId, t.department_id AS departmentId, t.job_id AS jobId, t.is_leader AS isLeader, t.create_time AS createTime, t.update_time AS updateTine, t.`password` AS userPwd, t3.id AS roleId, t3.`name` AS roleName, t3.description AS roleDescription, t3.seq AS roleSeq, t3.`status` AS roleStatus, t4.enterprise_name AS enterpriseName, t5.department_name AS departmentName, t6.job_name AS jobName, group_concat(t3.`name`) AS rolesList FROM t_sys_user t LEFT JOIN t_sys_user_role t2 ON t.id = t2.user_id LEFT JOIN t_sys_role t3 ON t2.role_id = t3.id LEFT JOIN t_enterprise t4 ON t.enterprise_id = t4.id LEFT JOIN t_enterprise_department t5 ON t.department_id = t5.id LEFT JOIN t_enterprise_job t6 ON t.job_id = t6.id
t.login_name like concat('%',#{name},'%') or t.name like concat('%',#{name},'%')
and t.enterprise_id = #{enterpriseId}
and t.department_id= #{departmentId}
and (t.create_time between concat(#{startTime},'00:00:00') and concat(#{endTime},"00:00:00"))
GROUP BY t.id
