DROP TABLE IF EXISTS `t_sys_schedule`;
CREATE TABLE `t_sys_schedule` (
  `id` bigint(20) NOT NULL,
  `type` varchar(255) DEFAULT NULL COMMENT '任务类别',
  `schedule_code` varchar(20) DEFAULT NULL COMMENT '任务代码（对应计算规则分类代码(取变量表的配置)）',
  `schedule_name` varchar(255) DEFAULT NULL COMMENT '任务名称',
  `expression` varchar(20) DEFAULT NULL COMMENT '任务调度表达式',
  `update_time` datetime DEFAULT NULL COMMENT '记录最后修改时间',
  `update_user` varchar(20) DEFAULT NULL COMMENT '记录最后修改者（用户）',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='定时任务配置表';

